wfQuery.fn.extend({_appender:function(e){return wfQuery(e)},append:function(e){return this.cross_each(this._appender(e),function(e,n){e.appendChild(n)})},prepend:function(e){return this.cross_each(this._appender(e),function(e,n){e.insertBefore(n,e.firstChild)})},before:function(e){return this.cross_each(this._appender(e),function(e,n){e.parentNode.insertBefore(n,e)})},after:function(e){return this.cross_each(this._appender(e),function(e,n){e.parentNode.insertBefore(n,e.nextSibling)})},empty:function(){return this.each(function(){this.textContent=""})},remove:function(){return this.each(function(){this.parentNode.removeChild(this)})},clone:function(){return wfQuery(this.map(function(e){return e.cloneNode?e.cloneNode(!0):e}))}});